const inputItems = $('Loop Over Items2').all();
const outputItems = [];

inputItems.forEach((item, idx) => {
	// Original card JSON from Loop Over Items2
	const c = item.json;

	// Build the transformed list element
	const transformed = {
		id: $node["Loop Over Items2"].context["currentRunIndex"] * $('Set Batch Size').first().json.batch_size + idx + 1,
		payload: {
			colors: c.colors,
			convertedManaCost: c.convertedManaCost,
			multiverseId: c.identifiers?.multiverseId,
			keywords: c.keywords,
			manaCost: c.manaCost,
			name: c.name,
			originalType: c.originalType,
			originalText: JSON.stringify(c.originalText),
			rarity: c.rarity,
			legalities_standard: c.legalities?.standard,
			legalities_modern: c.legalities?.modern,
			legalities_commander: c.legalities?.commander,
			setCode: c.setCode,
            edhrecRank: c.edhrecRank,
            edhrecSaltiness: c.edhrecSaltiness,
            power: c.power,
            toughness: c.toughness,
            types: c.types,
            subtypes: c.subtypes,

		},
		vector: {
			// Wrap the embedding array in another array as required
			semantic: $input.all()[idx].json.embedding,
		},
	};

	// Add to current batch
	outputItems.push(transformed);
});

return outputItems;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  if (item.json.hasOwnProperty('name')) {
    item.json.namePrepared = item.json.name + " | " + item.json.originalType + " | " + item.json.manaCost + " | " + item.json.rarity + " | " + $input.first().json.keywords;
  }
}

return $input.all();

