// Get the list the user sent in the form.
    // It might already be an array (['U','W']) or a single string ('U,W')
    const raw = $('Constants (Redis Keys)1').first().json.colors;
    const colours = Array.isArray(raw) ? raw : raw.split(',').map(c => c.trim());

    /* Build one match_text condition per colour and wrap them in `should`
       => the filter passes if *any* colour token is present in payload.colors
    */
    return {
      filters: {
        should: colours.map(c => ({
          key: 'colors',
          match: { text: c }      // full-text search on token “c”
        }))
      }
    };

